params.query:
-- Insert categories
insert into public.product_categories (name, icon, emoji) values
  ('Vegetables', 'carrot', '🥕'),
  ('Fruits', 'apple', '🍎'),
  ('Dairy', 'milk', '🥛'),
  ('Grains', 'grain', '🌾'),
  ('Meat', 'meat', '🥩')
on conflict (name) do nothing;

-- Insert sample farms
insert into public.farms (name, location, address, rating, hours, image, description) values
  ('Green Valley Farm', 'California (USA)', '123 Farm Road, California, USA', 4.7, '7am-7pm Daily', 'https://images.unsplash.com/photo-1625246333195-78d9c38ad449?w=800', 'Organic vegetables and fruits grown with care'),
  ('Sunny Acres', 'Florida (USA)', '456 Sunshine Lane, Florida, USA', 4.8, '6am-8pm Daily', 'https://images.unsplash.com/photo-1500382017468-9049fed747ef?w=800', 'Fresh produce delivered daily'),
  ('Akij Farm', 'California (USA)', '789 Green Lane, California, USA', 4.7, '6am-9pm Daily', 'https://images.unsplash.com/photo-1574943320219-553eb213f72d?w=800', 'Premium quality farm products'),
  ('Mountain Orchard', 'Washington (USA)', '321 Mountain View, Washington, USA', 4.9, '8am-6pm Daily', 'https://images.unsplash.com/photo-1560493676-04071c5f467b?w=800', 'Best apples in the region'),
  ('Berry Fields', 'California (USA)', '654 Berry Road, California, USA', 4.8, '7am-7pm Daily', 'https://images.unsplash.com/photo-1523348837708-15d4a09cfac2?w=800', 'Organic berries and seasonal fruits');

-- Get category IDs
do $$
declare
  vegetables_id uuid;
  fruits_id uuid;
  farm1_id uuid;
  farm2_id uuid;
  farm3_id uuid;
  farm4_id uuid;
  farm5_id uuid;
begin
  -- Get category IDs
  select id into vegetables_id from public.product_categories where name = 'Vegetables';
  select id into fruits_id from public.product_categories where name = 'Fruits';

  -- Get farm IDs
  select id into farm1_id from public.farms where name = 'Green Valley Farm';
  select id into farm2_id from public.farms where name = 'Sunny Acres';
  select id into farm3_id from public.farms where name = 'Akij Farm';
  select id into farm4_id from public.farms where name = 'Mountain Orchard';
  select id into farm5_id from public.farms where name = 'Berry Fields';

  -- Insert products
  insert into public.marketplace_products (farm_id, category_id, name, description, price, original_price, unit, rating, image, in_stock) values
    (farm1_id, vegetables_id, 'Seasonal Cabbage', 'Fresh organic cabbage harvested daily', 35, 39, 'Per kg', 4.7, 'https://images.unsplash.com/photo-1594282486552-05b4d80fbb9f?w=400', true),
    (farm2_id, vegetables_id, 'Red Tomatoes', 'Juicy red tomatoes perfect for salads', 28, null, 'Per kg', 4.8, 'https://images.unsplash.com/photo-1546470427-e26264be0b0d?w=400', true),
    (farm3_id, vegetables_id, 'Fresh Lettuce', 'Crisp lettuce for your healthy meals', 31, 39, 'Per kg', 4.7, 'https://images.unsplash.com/photo-1622206151226-18ca2c9ab4a1?w=400', true),
    (farm1_id, vegetables_id, 'Organic Carrots', 'Sweet organic carrots rich in vitamins', 25, null, 'Per kg', 4.6, 'https://images.unsplash.com/photo-1598170845058-32b9d6a5da37?w=400', true),
    (farm4_id, fruits_id, 'Fresh Apples', 'Crisp and sweet apples from our orchard', 45, null, 'Per kg', 4.9, 'https://images.unsplash.com/photo-1560806887-1e4cd0b6cbd6?w=400', true),
    (farm5_id, fruits_id, 'Organic Strawberries', 'Sweet organic strawberries picked fresh', 55, null, 'Per kg', 4.8, 'https://images.unsplash.com/photo-1464965911861-746a04b4bca6?w=400', true),
    (farm1_id, vegetables_id, 'Bell Peppers', 'Colorful bell peppers full of flavor', 42, null, 'Per kg', 4.5, 'https://images.unsplash.com/photo-1563565375-f3fdfdbefa83?w=400', true),
    (farm2_id, vegetables_id, 'Cucumbers', 'Fresh cucumbers perfect for salads', 22, 25, 'Per kg', 4.6, 'https://images.unsplash.com/photo-1604977042946-1eecc30f269e?w=400', true),
    (farm4_id, fruits_id, 'Oranges', 'Juicy oranges packed with vitamin C', 38, null, 'Per kg', 4.7, 'https://images.unsplash.com/photo-1547514701-42782101795e?w=400', true),
    (farm5_id, fruits_id, 'Blueberries', 'Fresh organic blueberries', 65, null, 'Per kg', 4.9, 'https://images.unsplash.com/photo-1498557850523-fd3d118b962e?w=400', true);
end $$;
